def hex_to_base4_int_array(hex_string):
    # Convert hex string to integer
    base_4_array = []
    for index in range(len(hex_string)):
        decimal_integer = int(hex_string[index], 16)
        base_4_array.append(decimal_integer%4)
        base_4_array.append(decimal_integer >> 2)



    return base_4_array
