def get_pick_frequency(fourier_transform,fft_relevent_range_low,fft_relevent_range_high,base_frequency):
    #print(fft_relevent_range_low,fft_relevent_range_high,base_frequency)
    cutted_fft = fourier_transform[fft_relevent_range_low:fft_relevent_range_high+1]
    for index in range(len(cutted_fft)):
        cutted_fft[index] = cutted_fft[index]*(fft_relevent_range_high-base_frequency)/(fft_relevent_range_low-base_frequency+index)
    return np.argmax(cutted_fft)+ fft_relevent_range_low , cutted_fft





