def generate_frequency_map(myArray, fft_size, iteration_label=""):
    fs = 800000  # Sampling frequency
    frequency_resolution = fs / fft_size

    #-----------------------------------------------------------
    #max_index = int(100000 / frequency_resolution)
    # -----------------------------------------------------------
    low_freq = 1e9-5e6
    high_freq = 1e9+5e6
    # -----------------------------------------------------------

    low_index = int(low_freq / frequency_resolution)
    high_index = int(high_freq / frequency_resolution)

    print("fft_size:", fft_size)

    # Zero-pad or truncate the signal to the required fft_size
    if len(myArray) < fft_size:
        myArray = np.pad(myArray, (0, fft_size - len(myArray)), mode='constant')
    else:
        myArray = myArray[:fft_size]


    # window = np.hanning(len(myArray))
    # signal_windowed = myArray * window
    # fourier_transform = np.abs(np.fft.fft(signal_windowed)) / len(signal_windowed)
    # --- Skip windowing: apply FFT directly ---
    fourier_transform = np.abs(np.fft.fft(myArray)) / len(myArray)

    # Plot FFT up to 32.5kHz
    plt.figure()

    # -----------------------------------------------------------
    # plt.plot(np.arange(max_index) * frequency_resolution, fourier_transform[:max_index])
    # -----------------------------------------------------------
    plt.plot(np.arange(low_index, high_index) * frequency_resolution,
             fourier_transform[low_index:high_index])
    # -----------------------------------------------------------

    plt.xlabel("Frequency (Hz)")
    plt.ylabel("Amplitude")
    plt.title("Full FFT Spectrum")
    plt.grid(True)


    # -----------------------------------------------------------
    # # Detect local maxima in combined FFT data up to 32.5kHz
    # x_vals = np.arange(max_index) * frequency_resolution
    # y_vals = fourier_transform[:max_index]
    # -----------------------------------------------------------
    x_vals = np.arange(low_index, high_index) * frequency_resolution
    y_vals = fourier_transform[low_index:high_index]
    # -----------------------------------------------------------

    filtered_peaks = []

    if len(y_vals) > 2:
        for i in range(1, len(y_vals) - 1):
            if y_vals[i] > y_vals[i - 1] and y_vals[i] > y_vals[i + 1] and y_vals[i] >= 2:
                filtered_peaks.append((x_vals[i], y_vals[i]))

    # Print and save true peaks
    if filtered_peaks:
        print("True FFT Peaks (local maxima with y ≥ 2):")
        for freq, amp in filtered_peaks:
            print(f"Freq: {freq:.2f} Hz, Peak Value: {amp:.2f}")

        # Append to CSV with iteration header
        file_path = "fft_peaks.csv"
        write_header = not os.path.exists(file_path)

        with open(file_path, "a", newline='') as csvfile:
            writer = csv.writer(csvfile)
            writer.writerow([f"Iteration: {iteration_label}"])
            writer.writerow(["Frequency_Hz", "Amplitude"])
            writer.writerows(filtered_peaks)
            writer.writerow([])  # Blank line between iterations
    else:
        print("No valid peaks (y ≥ 2) found.")

    plt.draw()
    #plt.show(block=False)  # Don't block execution
    #plt.savefig(f"fft_plot_iter_{i + 1}.png")
    plt.pause(0.001)  # Allow GUI event loop to update
    print("Show complete.")

    return -1
